import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';

const NavContainer = styled.nav`
  background: #111;
  padding: 1rem 2rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Logo = styled(Link)`
  color: #fff;
  text-decoration: none;
  font-size: 1.5rem;
  font-weight: bold;
  transition: color 0.2s;

  &:hover {
    color: #1db954;
  }
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;
`;

const NavLink = styled(Link)<{ active?: boolean }>`
  color: ${props => props.active ? '#1db954' : '#fff'};
  text-decoration: none;
  transition: color 0.2s;

  &:hover {
    color: #1db954;
  }
`;

export const Navbar: React.FC = () => {
  const location = useLocation();

  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <NavContainer>
      <Logo to="/">音乐可视化</Logo>
      <NavLinks>
        <NavLink to="/" active={isActive('/')}>首页</NavLink>
        <NavLink to="/visualizer" active={isActive('/visualizer')}>可视化</NavLink>
        <NavLink to="/gallery" active={isActive('/gallery')}>作品库</NavLink>
        <NavLink to="/profile" active={isActive('/profile')}>个人中心</NavLink>
      </NavLinks>
    </NavContainer>
  );
};
