// 定义创意视觉效果

import * as THREE from 'three';
import { getGradientColor } from './visualEffects';

// 3D球体效果
export interface SphereVisualizerOptions {
  startColor?: string;
  endColor?: string;
  detail?: number;
  distortFactor?: number;
}

export class SphereVisualizer {
  private scene: THREE.Scene;
  private camera: THREE.PerspectiveCamera;
  private renderer: THREE.WebGLRenderer;
  private sphere: THREE.Mesh;
  private geometry: THREE.IcosahedronGeometry;
  private options: Required<SphereVisualizerOptions>;
  private originalPositions: Float32Array;
  private rotating: boolean = true;
  private material: THREE.MeshPhongMaterial;
  private light: THREE.DirectionalLight;
  private rotationSpeed: number;

  constructor(canvas: HTMLCanvasElement) {
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(75, canvas.width / canvas.height, 0.1, 1000);
    this.renderer = new THREE.WebGLRenderer({ canvas, alpha: true });
    this.renderer.setSize(canvas.width, canvas.height);

    // 创建球体
    const geometry = new THREE.SphereGeometry(2, 32, 32);
    this.material = new THREE.MeshPhongMaterial({
      color: 0x1db954,
      shininess: 100,
      specular: 0x1ed760,
    });
    this.sphere = new THREE.Mesh(geometry, this.material);
    this.scene.add(this.sphere);

    // 添加光源
    this.light = new THREE.DirectionalLight(0xffffff, 1);
    this.light.position.set(5, 5, 5);
    this.scene.add(this.light);

    // 设置相机位置
    this.camera.position.z = 5;

    this.rotationSpeed = 0.005;
  }

  draw(frequencies: Uint8Array) {
    // 计算平均频率
    let sum = 0;
    for (let i = 0; i < frequencies.length; i++) {
      sum += frequencies[i];
    }
    const average = sum / frequencies.length;
    const normalizedAverage = average / 255;

    // 更新球体
    this.sphere.rotation.x += this.rotationSpeed;
    this.sphere.rotation.y += this.rotationSpeed;

    // 根据音频数据调整球体
    const scale = 1 + normalizedAverage * 0.5;
    this.sphere.scale.set(scale, scale, scale);

    // 更新材质颜色
    const hue = (Date.now() * 0.001) % 1;
    const color = new THREE.Color().setHSL(hue, 0.7, 0.5);
    this.material.color = color;

    // 渲染场景
    this.renderer.render(this.scene, this.camera);
  }

  dispose() {
    this.renderer.dispose();
    this.material.dispose();
    this.sphere.geometry.dispose();
  }
}

// 频谱瀑布效果
export interface WaterfallOptions {
  startColor?: string;
  endColor?: string;
  fadeSpeed?: number;
  maxHistory?: number;
}

export class SpectrumWaterfall {
  private canvas: HTMLCanvasElement;
  private ctx: CanvasRenderingContext2D;
  private options: Required<WaterfallOptions>;
  private history: Uint8Array[] = [];
  
  constructor(canvas: HTMLCanvasElement, options: WaterfallOptions = {}) {
    this.canvas = canvas;
    this.ctx = canvas.getContext('2d')!;
    
    this.options = {
      startColor: options.startColor || '#9c27b0',
      endColor: options.endColor || '#3f51b5',
      fadeSpeed: options.fadeSpeed || 0.05,
      maxHistory: options.maxHistory || 100
    };
  }
  
  update(frequencies: Uint8Array): void {
    // 添加当前频谱数据到历史记录
    this.history.unshift(new Uint8Array(frequencies));
    
    // 限制历史记录长度
    if (this.history.length > this.options.maxHistory) {
      this.history.pop();
    }
    
    // 清除画布
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
    
    // 计算每条历史记录的高度
    const rowHeight = this.canvas.height / this.options.maxHistory;
    
    // 绘制历史频谱
    this.history.forEach((freqs, index) => {
      const y = index * rowHeight;
      
      // 绘制频谱行
      const barWidth = this.canvas.width / freqs.length;
      
      for (let i = 0; i < freqs.length; i++) {
        const value = freqs[i] / 255;
        const x = i * barWidth;
        
        // 根据值和行索引计算颜色
        const fadeAlpha = 1 - (index / this.options.maxHistory);
        const color = getGradientColor(
          this.options.startColor, 
          this.options.endColor, 
          value
        );
        
        // 设置颜色和透明度
        this.ctx.fillStyle = color;
        this.ctx.globalAlpha = fadeAlpha;
        
        // 绘制矩形
        this.ctx.fillRect(x, y, barWidth, rowHeight);
      }
    });
    
    // 重置透明度
    this.ctx.globalAlpha = 1;
  }
  
  resize(width: number, height: number): void {
    this.canvas.width = width;
    this.canvas.height = height;
  }
}

// 星空效果
export interface Star {
  x: number;
  y: number;
  size: number;
  color: string;
  speed: number;
  brightness: number;
  pulseFactor: number;
}

export interface StarfieldOptions {
  starCount?: number;
  startColor?: string;
  endColor?: string;
  maxSpeed?: number;
  minSize?: number;
  maxSize?: number;
}

export class Starfield {
  private canvas: HTMLCanvasElement;
  private ctx: CanvasRenderingContext2D;
  private stars: Star[] = [];
  private options: Required<StarfieldOptions>;
  private rotation: number = 0;
  private centerX: number;
  private centerY: number;
  
  constructor(canvas: HTMLCanvasElement, options: StarfieldOptions = {}) {
    this.canvas = canvas;
    this.ctx = canvas.getContext('2d')!;
    
    this.options = {
      starCount: options.starCount || 200,
      startColor: options.startColor || '#9c27b0',
      endColor: options.endColor || '#3f51b5',
      maxSpeed: options.maxSpeed || 1.5,
      minSize: options.minSize || 1,
      maxSize: options.maxSize || 3
    };
    
    this.centerX = this.canvas.width / 2;
    this.centerY = this.canvas.height / 2;
    
    // 初始化星星
    this.initStars();
    
    // 监听画布大小变化
    window.addEventListener('resize', () => this.handleResize());
  }
  
  private initStars(): void {
    this.stars = [];
    this.centerX = this.canvas.width / 2;
    this.centerY = this.canvas.height / 2;
    
    const maxRadius = Math.sqrt(
      Math.pow(this.canvas.width, 2) + Math.pow(this.canvas.height, 2)
    ) / 2;
    
    for (let i = 0; i < this.options.starCount; i++) {
      // 随机角度和距离中心的半径
      const angle = Math.random() * Math.PI * 2;
      const radius = Math.random() * maxRadius;
      
      // 计算坐标
      const x = this.centerX + Math.cos(angle) * radius;
      const y = this.centerY + Math.sin(angle) * radius;
      
      // 创建星星
      this.stars.push({
        x,
        y,
        size: this.options.minSize + Math.random() * (this.options.maxSize - this.options.minSize),
        color: getGradientColor(
          this.options.startColor, 
          this.options.endColor, 
          Math.random()
        ),
        speed: (0.1 + Math.random() * this.options.maxSpeed) * (radius / maxRadius),
        brightness: 0.2 + Math.random() * 0.8,
        pulseFactor: 0.5 + Math.random() * 0.5,
      });
    }
  }
  
  update(frequencies: Uint8Array): void {
    // 计算平均音量
    let sum = 0;
    for (let i = 0; i < frequencies.length; i++) {
      sum += frequencies[i];
    }
    const avgVolume = sum / frequencies.length / 255;
    
    // 清除画布
    this.ctx.fillStyle = '#000';
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    // 根据音量调整旋转速度
    this.rotation += 0.001 + avgVolume * 0.01;
    
    // 更新和绘制星星
    this.stars.forEach((star, index) => {
      // 获取对应频率带的值
      const freqIndex = Math.floor((index / this.stars.length) * frequencies.length);
      const frequency = frequencies[freqIndex] / 255;
      
      // 计算到中心的距离
      const dx = star.x - this.centerX;
      const dy = star.y - this.centerY;
      const distance = Math.sqrt(dx * dx + dy * dy);
      
      // 旋转星星
      const angle = Math.atan2(dy, dx) + this.rotation * star.speed;
      const newDistance = distance + (avgVolume * 20 * star.pulseFactor) - 10;
      
      // 更新位置
      star.x = this.centerX + Math.cos(angle) * newDistance;
      star.y = this.centerY + Math.sin(angle) * newDistance;
      
      // 如果星星离开画布，重置它
      if (
        star.x < 0 || 
        star.x > this.canvas.width || 
        star.y < 0 || 
        star.y > this.canvas.height
      ) {
        const newAngle = Math.random() * Math.PI * 2;
        const maxRadius = Math.sqrt(
          Math.pow(this.canvas.width, 2) + Math.pow(this.canvas.height, 2)
        ) / 2;
        const newRadius = Math.random() * maxRadius;
        
        star.x = this.centerX + Math.cos(newAngle) * newRadius;
        star.y = this.centerY + Math.sin(newAngle) * newRadius;
      }
      
      // 根据频率和音量调整大小和亮度
      const brightness = star.brightness + frequency * 0.5;
      const size = star.size * (1 + frequency * 2);
      
      // 绘制星星
      this.ctx.fillStyle = star.color;
      this.ctx.globalAlpha = brightness;
      this.ctx.beginPath();
      this.ctx.arc(star.x, star.y, size, 0, Math.PI * 2);
      this.ctx.fill();
      
      // 绘制光芒
      if (frequency > 0.6) {
        this.ctx.globalAlpha = brightness * 0.4;
        this.ctx.beginPath();
        this.ctx.arc(star.x, star.y, size * 2, 0, Math.PI * 2);
        this.ctx.fill();
      }
    });
    
    // 重置透明度
    this.ctx.globalAlpha = 1;
  }
  
  handleResize(): void {
    this.centerX = this.canvas.width / 2;
    this.centerY = this.canvas.height / 2;
  }
  
  resize(width: number, height: number): void {
    this.canvas.width = width;
    this.canvas.height = height;
    this.initStars();
  }
}
