import React from 'react';
import styled from 'styled-components';
import { useAudioContext } from '../context/AudioContext';

const PlayerContainer = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, 0.8);
  padding: 1rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  z-index: 1000;
`;

const TrackInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  color: white;
  flex: 1;
`;

const Controls = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const Button = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  padding: 0.5rem;
  font-size: 1.2rem;
  transition: color 0.2s;

  &:hover {
    color: #1db954;
  }
`;

const ProgressBar = styled.div`
  flex: 1;
  height: 4px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 2px;
  margin: 0 1rem;
  cursor: pointer;
  position: relative;

  &:hover {
    height: 6px;
  }
`;

const Progress = styled.div<{ progress: number }>`
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;
  width: ${props => props.progress}%;
  background: #1db954;
  border-radius: 2px;
`;

const VolumeControl = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: white;
`;

const VolumeSlider = styled.input`
  width: 100px;
`;

const TimeDisplay = styled.div`
  color: white;
  font-size: 0.9rem;
`;

const formatTime = (time: number): string => {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${minutes}:${seconds.toString().padStart(2, '0')}`;
};

export const MiniPlayer: React.FC = () => {
  const {
    audioData,
    play,
    pause,
    stop,
    seek,
    setVolume,
  } = useAudioContext();

  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = x / rect.width;
    seek(percentage * audioData.duration);
  };

  const handleVolumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const volume = parseFloat(e.target.value);
    setVolume(volume);
  };

  return (
    <PlayerContainer>
      <TrackInfo>
        <div>
          <div>{audioData.title || '未选择音乐'}</div>
          <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>{audioData.artist}</div>
        </div>
      </TrackInfo>

      <Controls>
        <Button onClick={stop}>⏹</Button>
        <Button onClick={audioData.isPlaying ? pause : play}>
          {audioData.isPlaying ? '⏸' : '▶'}
        </Button>
      </Controls>

      <ProgressBar onClick={handleProgressClick}>
        <Progress progress={(audioData.currentTime / audioData.duration) * 100} />
      </ProgressBar>

      <TimeDisplay>
        {formatTime(audioData.currentTime)} / {formatTime(audioData.duration)}
      </TimeDisplay>

      <VolumeControl>
        <span>🔊</span>
        <VolumeSlider
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={audioData.volume}
          onChange={handleVolumeChange}
        />
      </VolumeControl>
    </PlayerContainer>
  );
};
