import React, { createContext, useContext, useState, useRef, useEffect } from 'react';

// 音频数据类型定义
interface AudioData {
  title: string;
  artist: string;
  duration: number;
  currentTime: number;
  isPlaying: boolean;
  volume: number;
}

// 音频上下文接口
interface AudioContextType {
  audioData: AudioData;
  loadAudio: (file: File) => Promise<void>;
  loadAudioFromUrl: (url: string) => Promise<void>;
  play: () => void;
  pause: () => void;
  stop: () => void;
  seek: (time: number) => void;
  setVolume: (volume: number) => void;
  audioContext: AudioContext | null;
  analyser: AnalyserNode | null;
  audioElement: HTMLAudioElement | null;
  isPlaying: boolean;
  currentTime: number;
  duration: number;
  volume: number;
  currentTrack: string;
}

// 创建上下文
const AudioContextProvider = createContext<AudioContextType | null>(null);

// Provider组件
export const AudioProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [audioData, setAudioData] = useState<AudioData>({
    title: '',
    artist: '',
    duration: 0,
    currentTime: 0,
    isPlaying: false,
    volume: 1,
  });

  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const audioElementRef = useRef<HTMLAudioElement | null>(null);
  const sourceNodeRef = useRef<MediaElementAudioSourceNode | null>(null);

  useEffect(() => {
    audioContextRef.current = new window.AudioContext();
    analyserRef.current = audioContextRef.current.createAnalyser();
    analyserRef.current.fftSize = 2048;

    return () => {
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, []);

  const loadAudio = async (file: File) => {
    const url = URL.createObjectURL(file);
    const audio = new Audio(url);
    audioElementRef.current = audio;

    audio.addEventListener('loadedmetadata', () => {
      setAudioData(prev => ({
        ...prev,
        title: file.name,
        artist: 'Unknown Artist',
        duration: audio.duration,
      }));
    });

    audio.addEventListener('timeupdate', () => {
      setAudioData(prev => ({
        ...prev,
        currentTime: audio.currentTime,
      }));
    });

    audio.addEventListener('ended', () => {
      setAudioData(prev => ({
        ...prev,
        isPlaying: false,
        currentTime: 0,
      }));
    });

    if (sourceNodeRef.current) {
      sourceNodeRef.current.disconnect();
    }

    sourceNodeRef.current = audioContextRef.current!.createMediaElementSource(audio);
    sourceNodeRef.current.connect(analyserRef.current!);
    analyserRef.current!.connect(audioContextRef.current!.destination);
  };

  const loadAudioFromUrl = async (url: string) => {
    const audio = new Audio(url);
    audioElementRef.current = audio;

    audio.addEventListener('loadedmetadata', () => {
      setAudioData(prev => ({
        ...prev,
        title: url.split('/').pop() || 'Unknown Track',
        artist: 'Unknown Artist',
        duration: audio.duration,
      }));
    });

    audio.addEventListener('timeupdate', () => {
      setAudioData(prev => ({
        ...prev,
        currentTime: audio.currentTime,
      }));
    });

    audio.addEventListener('ended', () => {
      setAudioData(prev => ({
        ...prev,
        isPlaying: false,
        currentTime: 0,
      }));
    });

    if (sourceNodeRef.current) {
      sourceNodeRef.current.disconnect();
    }

    sourceNodeRef.current = audioContextRef.current!.createMediaElementSource(audio);
    sourceNodeRef.current.connect(analyserRef.current!);
    analyserRef.current!.connect(audioContextRef.current!.destination);
  };

  const play = () => {
    if (audioElementRef.current) {
      audioElementRef.current.play();
      setAudioData(prev => ({ ...prev, isPlaying: true }));
    }
  };

  const pause = () => {
    if (audioElementRef.current) {
      audioElementRef.current.pause();
      setAudioData(prev => ({ ...prev, isPlaying: false }));
    }
  };

  const stop = () => {
    if (audioElementRef.current) {
      audioElementRef.current.pause();
      audioElementRef.current.currentTime = 0;
      setAudioData(prev => ({
        ...prev,
        isPlaying: false,
        currentTime: 0,
      }));
    }
  };

  const seek = (time: number) => {
    if (audioElementRef.current) {
      audioElementRef.current.currentTime = time;
      setAudioData(prev => ({ ...prev, currentTime: time }));
    }
  };

  const setVolume = (volume: number) => {
    if (audioElementRef.current) {
      audioElementRef.current.volume = volume;
      setAudioData(prev => ({ ...prev, volume }));
    }
  };

  return (
    <AudioContextProvider.Provider
      value={{
        audioData,
        loadAudio,
        loadAudioFromUrl,
        play,
        pause,
        stop,
        seek,
        setVolume,
        audioContext: audioContextRef.current,
        analyser: analyserRef.current,
        audioElement: audioElementRef.current,
        isPlaying: audioData.isPlaying,
        currentTime: audioData.currentTime,
        duration: audioData.duration,
        volume: audioData.volume,
        currentTrack: audioData.title
      }}
    >
      {children}
    </AudioContextProvider.Provider>
  );
};

// 使用上下文的Hook
export const useAudioContext = () => {
  const context = useContext(AudioContextProvider);
  if (!context) {
    throw new Error('useAudioContext must be used within an AudioProvider');
  }
  return context;
};

// 迷你播放器组件，固定在屏幕底部
export const MiniPlayer: React.FC = () => {
  const { 
    isPlaying, 
    currentTrack, 
    play, 
    pause, 
    currentTime, 
    duration,
    seek,
    volume,
    setVolume
  } = useAudioContext();

  // 如果没有正在播放的曲目，不显示迷你播放器
  if (!currentTrack) return null;

  // 格式化时间显示
  const formatTime = (time: number) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  // 计算进度百分比
  const progressPercent = ((currentTime / duration) * 100 || 0) + '%';

  return (
    <div style={{
      position: 'fixed',
      bottom: 0,
      left: 0,
      right: 0,
      backgroundColor: 'rgba(26, 26, 26, 0.95)',
      padding: '10px 20px',
      zIndex: 1000,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      backdropFilter: 'blur(10px)'
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
        <button 
          onClick={isPlaying ? pause : play}
          style={{
            backgroundColor: '#9c27b0',
            color: 'white',
            border: 'none',
            borderRadius: '50%',
            width: '36px',
            height: '36px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer'
          }}
        >
          {isPlaying ? '❚❚' : '▶'}
        </button>
        <div>
          <div style={{ color: 'white', fontSize: '0.9rem', marginBottom: '4px' }}>
            {currentTrack}
          </div>
          <div style={{ color: '#aaa', fontSize: '0.8rem' }}>
            {formatTime(currentTime)} / {formatTime(duration)}
          </div>
        </div>
      </div>

      <div style={{ flex: 1, padding: '0 20px', cursor: 'pointer' }} 
           onClick={(e) => {
             const rect = e.currentTarget.getBoundingClientRect();
             const offsetX = e.clientX - rect.left;
             const percentage = offsetX / rect.width;
             seek(percentage * duration);
           }}>
        <div style={{ 
          height: '4px', 
          backgroundColor: '#333',
          borderRadius: '2px',
          position: 'relative'
        }}>
          <div style={{
            position: 'absolute',
            top: 0,
            left: 0,
            height: '100%',
            width: progressPercent,
            backgroundColor: '#ba68c8',
            borderRadius: '2px'
          }} />
        </div>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', gap: '10px', width: '120px' }}>
        <span style={{ color: '#aaa', fontSize: '0.8rem' }}>
          {Math.round(volume * 100)}%
        </span>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={(e) => setVolume(parseFloat(e.target.value))}
          style={{ width: '80px' }}
        />
      </div>
    </div>
  );
};
