import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import { useAudioContext } from '../context/AudioContext';
import * as visualEffects from '../utils/newVisualEffects';
import * as newVisualEffects from '../utils/newVisualEffects';

// スタイルコンポーネント
const VisualizerContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Title = styled.h1`
  color: #ba68c8;
  margin-bottom: 20px;
  text-align: center;
`;

const ControlPanel = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #1e1e1e;
  padding: 20px;
  border-radius: 8px;
  margin-bottom: 30px;
`;

const ControlRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const ControlGroup = styled.div`
  flex: 1;
  min-width: 200px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #ddd;
  font-weight: 500;
`;

const Button = styled.button<{ $primary?: boolean }>`
  background-color: ${props => props.$primary ? '#9c27b0' : '#333'};
  color: white;
  border: none;
  padding: 10px 15px;
  border-radius: 4px;
  cursor: pointer;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${props => props.$primary ? '#ba68c8' : '#444'};
  }

  &:disabled {
    background-color: #555;
    cursor: not-allowed;
  }
`;

const FileInput = styled.input`
  display: none;
`;

const FileInputLabel = styled.label`
  display: inline-block;
  background-color: #333;
  color: white;
  padding: 10px 15px;
  border-radius: 4px;
  cursor: pointer;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background-color: #444;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 10px;
  background-color: #333;
  color: white;
  border: none;
  border-radius: 4px;
  appearance: none;
  cursor: pointer;

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px rgba(186, 104, 200, 0.3);
  }
`;

const ColorInput = styled.input`
  width: 40px;
  height: 40px;
  padding: 0;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  background-color: transparent;

  &::-webkit-color-swatch-wrapper {
    padding: 0;
  }

  &::-webkit-color-swatch {
    border: none;
    border-radius: 4px;
  }
`;

const ColorInputGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 6px;
  background-color: #333;
  border-radius: 3px;
  margin-top: 5px;
  position: relative;
  cursor: pointer;
`;

const Progress = styled.div<{ $width: string }>`
  height: 100%;
  width: ${props => props.$width};
  background-color: #ba68c8;
  border-radius: 3px;
`;

const CanvasContainer = styled.div`
  width: 100%;
  height: 500px;
  overflow: hidden;
  border-radius: 8px;
  background-color: #111;
  position: relative;
`;

const Canvas = styled.canvas`
  width: 100%;
  height: 100%;
  display: block;
`;

// 视觉化效果类型
enum VisualizationType {
  SPHERE_3D = 'sphere3d',
  SPECTRUM_WATERFALL = 'spectrumWaterfall',
  STAR_FIELD = 'starField'
}

// サンプル音楽リスト
const SAMPLE_TRACKS = [
  { name: 'サンプル音楽 1', url: 'https://assets.codepen.io/4358584/Anitek_-_Komorebi.mp3' },
  { name: 'サンプル音楽 2', url: 'https://assets.codepen.io/4358584/Moonbase_-_Dinos.mp3' },
  { name: 'サンプル音楽 3', url: 'https://assets.codepen.io/4358584/n-Dimensions_-_Cyber_Diva.mp3' }
];

function VisualizerPage() {
  // 全局音频上下文
  const { 
    audioData, 
    isPlaying, 
    loadAudio, 
    loadAudioFromUrl, 
    play, 
    pause, 
    stop, 
    setVolume,
    currentTime,
    duration,
    volume,
    seek,
    analyser
  } = useAudioContext();

  // 状态管理
  const [visualType, setVisualType] = useState<VisualizationType>(VisualizationType.SPHERE_3D);
  const [startColor, setStartColor] = useState('#9c27b0');
  const [endColor, setEndColor] = useState('#3f51b5');
  const [fileName, setFileName] = useState('未选择文件');

  // Refs
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const canvasCtxRef = useRef<CanvasRenderingContext2D | null>(null);
  const animationRef = useRef<number | null>(null);

  // 各种可视化效果的状态
  const particlesRef = useRef<visualEffects.Particle[]>([]);
  const sphereVisualizerRef = useRef<visualEffects.SphereVisualizer | null>(null);
  const spectrumWaterfallRef = useRef<visualEffects.SpectrumWaterfall | null>(null);
  const starfieldRef = useRef<visualEffects.Starfield | null>(null);

  // 初始化画布
  useEffect(() => {
    if (canvasRef.current) {
      canvasCtxRef.current = canvasRef.current.getContext('2d');

      // 设置画布大小
      const resizeCanvas = () => {
        if (canvasRef.current) {
          const container = canvasRef.current.parentElement;
          if (container) {
            canvasRef.current.width = container.clientWidth;
            canvasRef.current.height = container.clientHeight;
          }
        }
      };

      resizeCanvas();
      window.addEventListener('resize', resizeCanvas);

      // 清理之前的可视化效果
      if (sphereVisualizerRef.current) {
        sphereVisualizerRef.current.dispose();
        sphereVisualizerRef.current = null;
      }

      // 初始化新的可视化效果
      if (visualType === VisualizationType.SPHERE_3D && canvasRef.current) {
        sphereVisualizerRef.current = new visualEffects.SphereVisualizer(canvasRef.current);
      } else if (visualType === VisualizationType.SPECTRUM_WATERFALL && canvasRef.current) {
        spectrumWaterfallRef.current = new visualEffects.SpectrumWaterfall(canvasRef.current);
      } else if (visualType === VisualizationType.STAR_FIELD && canvasRef.current) {
        starfieldRef.current = new visualEffects.Starfield(canvasRef.current);
      }

      return () => {
        window.removeEventListener('resize', resizeCanvas);

        // 清理可视化效果
        if (sphereVisualizerRef.current) {
          sphereVisualizerRef.current.dispose();
          sphereVisualizerRef.current = null;
        }
        if (spectrumWaterfallRef.current) {
          spectrumWaterfallRef.current.dispose();
          spectrumWaterfallRef.current = null;
        }
        if (starfieldRef.current) {
          starfieldRef.current.dispose();
          starfieldRef.current = null;
        }
      };
    }
  }, [visualType]);

  // 绘制可视化效果
  useEffect(() => {
    if (!audioData || !canvasRef.current) {
      return;
    }

    const canvas = canvasRef.current;
    const ctx = canvasCtxRef.current;

    const draw = () => {
      if (!canvas || !ctx) return;

      const frequencies = new Uint8Array(analyser!.frequencyBinCount);
      analyser!.getByteFrequencyData(frequencies);

      switch (visualType) {
        case VisualizationType.SPHERE_3D:
          if (sphereVisualizerRef.current) {
            sphereVisualizerRef.current.draw(frequencies);
          }
          break;

        case VisualizationType.SPECTRUM_WATERFALL:
          if (spectrumWaterfallRef.current) {
            spectrumWaterfallRef.current.draw(frequencies);
          }
          break;

        case VisualizationType.STAR_FIELD:
          if (starfieldRef.current) {
            starfieldRef.current.draw(frequencies);
          }
          break;
      }

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [audioData, visualType, startColor, endColor, analyser]);

  // 文件名の更新を処理する
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      setFileName(file.name);
      loadAudio(file);
    }
  };

  // サンプル音楽選択処理
  const handleSampleTrack = (url: string) => {
    loadAudioFromUrl(url);
    const track = SAMPLE_TRACKS.find(t => t.url === url);
    if (track) {
      setFileName(track.name);
    }
  };

  // 進行状況クリック処理
  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (!duration) return;

    const progressBar = e.currentTarget;
    const rect = progressBar.getBoundingClientRect();
    const offsetX = e.clientX - rect.left;
    const percentage = offsetX / rect.width;

    const newTime = percentage * duration;
    seek(newTime);
  };

  // 音量変更処理
  const handleVolumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
  };

  // 時間表示フォーマット
  const formatTime = (time: number) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  return (
    <VisualizerContainer>
      <Title>音楽可視化</Title>

      <ControlPanel>
        <ControlRow>
          <ControlGroup>
            <Label>1. 音楽を選択</Label>
            <FileInputLabel>
              <FileInput 
                type="file" 
                accept="audio/*" 
                onChange={handleFileChange} 
              />
              ファイルを選択
            </FileInputLabel>
            <span style={{ marginLeft: '10px', color: '#999' }}>{fileName}</span>
          </ControlGroup>

          <ControlGroup>
            <Label>サンプル音楽</Label>
            <Select onChange={(e) => handleSampleTrack(e.target.value)}>
              <option value="">サンプル音楽を選択</option>
              {SAMPLE_TRACKS.map((track, index) => (
                <option key={index} value={track.url}>
                  {track.name}
                </option>
              ))}
            </Select>
          </ControlGroup>
        </ControlRow>

        <ControlRow>
          <ControlGroup>
            <Label>2. 再生コントロール</Label>
            <div style={{ display: 'flex', gap: '10px' }}>
              <Button $primary onClick={play} disabled={!audioData.ready}>
                再生
              </Button>
              <Button onClick={pause} disabled={!isPlaying}>
                一時停止
              </Button>
              <Button onClick={stop} disabled={!audioData.ready}>
                停止
              </Button>
            </div>
          </ControlGroup>

          <ControlGroup>
            <Label>音量：{Math.round(volume * 100)}%</Label>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(parseFloat(e.target.value))}
              style={{ width: '100%' }}
            />
          </ControlGroup>
        </ControlRow>

        <ControlRow>
          <ControlGroup>
            <Label>進行状況：{formatTime(currentTime)} / {formatTime(duration)}</Label>
            <ProgressBar onClick={handleProgressClick}>
              <Progress $width={`${(currentTime / duration) * 100 || 0}%`} />
            </ProgressBar>
          </ControlGroup>
        </ControlRow>

        <ControlRow>
          <ControlGroup>
            <Label>3. 視覚化効果を選択</Label>
            <Select 
              value={visualType} 
              onChange={(e) => setVisualType(e.target.value as VisualizationType)}
            >
              <option value={VisualizationType.SPHERE_3D}>3D球体反応</option>
              <option value={VisualizationType.SPECTRUM_WATERFALL}>周波数瀑布流</option>
              <option value={VisualizationType.STAR_FIELD}>星系効果</option>
            </Select>
          </ControlGroup>

          <ControlGroup>
            <Label>カスタムカラー</Label>
            <ColorInputGroup>
              <ColorInput 
                type="color" 
                value={startColor} 
                onChange={(e) => setStartColor(e.target.value)} 
              />
              <span style={{ color: '#999' }}>から</span>
              <ColorInput 
                type="color" 
                value={endColor} 
                onChange={(e) => setEndColor(e.target.value)} 
              />
            </ColorInputGroup>
          </ControlGroup>
        </ControlRow>
      </ControlPanel>

      <CanvasContainer>
        <Canvas ref={canvasRef} />
      </CanvasContainer>
    </VisualizerContainer>
  );
}

export { VisualizerPage };
