// 定义创意视觉效果

import * as THREE from 'three';
import { getGradientColor } from './visualEffects';

// 3D球体效果
export interface SphereVisualizerOptions {
  startColor?: string;
  endColor?: string;
  detail?: number;
  distortFactor?: number;
}

