  private scene: THREE.Scene;
  private camera: THREE.PerspectiveCamera;
  private renderer: THREE.WebGLRenderer;
  private sphere: THREE.Mesh;
  private geometry: THREE.IcosahedronGeometry;
  private options: Required<SphereVisualizerOptions>;
  private originalPositions: Float32Array;
  private rotating: boolean = true;
  private material: THREE.MeshPhongMaterial;
  private light: THREE.DirectionalLight;
  private rotationSpeed: number;

  constructor(container: HTMLElement, options: SphereVisualizerOptions = {}) {
    this.options = {
      startColor: options.startColor || '#9c27b0',
      endColor: options.endColor || '#3f51b5',
      detail: options.detail || 4,
      distortFactor: options.distortFactor || 0.3
    };

    // 创建THREE.js场景
    this.scene = new THREE.Scene();
    
    // 创建相机
    this.camera = new THREE.PerspectiveCamera(
      75, 
      container.clientWidth / container.clientHeight, 
      0.1, 
      1000
    );
    this.camera.position.z = 2.5;
    
    // 创建渲染器
    this.renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: true 
    });
    this.renderer.setSize(container.clientWidth, container.clientHeight);
    container.appendChild(this.renderer.domElement);
    
    // 创建球体几何体
    this.geometry = new THREE.IcosahedronGeometry(1, this.options.detail);
    
    // 保存原始顶点位置
    this.originalPositions = new Float32Array(this.geometry.attributes.position.array);
    
    // 创建材质
    this.material = new THREE.MeshPhongMaterial({
      color: this.options.startColor,
      wireframe: false,
      shininess: 100,
      emissive: '#111',
      specular: '#fff'
    });
    
    // 创建网格
    this.sphere = new THREE.Mesh(this.geometry, this.material);
    this.scene.add(this.sphere);
    
    // 添加灯光
    this.light = new THREE.DirectionalLight(0xffffff, 1);
    this.light.position.set(5, 5, 5);
    this.scene.add(this.light);
    
    // 自适应调整大小
    window.addEventListener('resize', () => {
      this.camera.aspect = container.clientWidth / container.clientHeight;
      this.camera.updateProjectionMatrix();
      this.renderer.setSize(container.clientWidth, container.clientHeight);
    });

    this.rotationSpeed = 0.005;
  }
  
  update(frequencies: Uint8Array): void {
    if (!this.sphere) return;
    
    // 计算平均音量
    let sum = 0;
    for (let i = 0; i < frequencies.length; i++) {
      sum += frequencies[i];
    }
    const avgVolume = sum / frequencies.length / 255;
    
    // 扭曲球体
    const positions = this.geometry.attributes.position.array as Float32Array;
    
    for (let i = 0; i < positions.length; i += 3) {
      const freqIndex = Math.floor((i / positions.length) * frequencies.length);
      const frequency = frequencies[freqIndex] / 255;
      
      // 使用原始位置和音频数据来扭曲顶点
      positions[i] = this.originalPositions[i] * (1 + frequency * this.options.distortFactor);
      positions[i + 1] = this.originalPositions[i + 1] * (1 + frequency * this.options.distortFactor);
      positions[i + 2] = this.originalPositions[i + 2] * (1 + frequency * this.options.distortFactor);
    }
    
    this.geometry.attributes.position.needsUpdate = true;
    
    // 根据音量更新材质颜色
    this.material.color.set(getGradientColor(
      this.options.startColor, 
      this.options.endColor, 
      avgVolume
    ));
    this.material.emissiveIntensity = avgVolume * 0.5;
    
    // 旋转球体
    if (this.rotating) {
      this.sphere.rotation.x += this.rotationSpeed;
      this.sphere.rotation.y += this.rotationSpeed;
    }
    
    // 渲染场景
    this.renderer.render(this.scene, this.camera);
  }
  
  resize(width: number, height: number): void {
    this.camera.aspect = width / height;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(width, height);
  }
  
  destroy(): void {
    // 移除渲染器
    if (this.renderer && this.renderer.domElement.parentNode) {
      this.renderer.domElement.parentNode.removeChild(this.renderer.domElement);
    }
    
    // 释放资源
    this.geometry.dispose();
    this.material.dispose();
    
    // 移除事件监听器
    window.removeEventListener('resize', this.resize);
  }
}

// 频谱瀑布效果
export interface WaterfallOptions {
  startColor?: string;
  endColor?: string;
  fadeSpeed?: number;
  maxHistory?: number;
}

export class SpectrumWaterfall {
  private canvas: HTMLCanvasElement;
  private ctx: CanvasRenderingContext2D;
  private options: Required<WaterfallOptions>;
  private history: Uint8Array[] = [];
  
  constructor(canvas: HTMLCanvasElement, options: WaterfallOptions = {}) {
    this.canvas = canvas;
    this.ctx = canvas.getContext('2d')!;
    
    this.options = {
      startColor: options.startColor || '#9c27b0',
      endColor: options.endColor || '#3f51b5',
      fadeSpeed: options.fadeSpeed || 0.05,
      maxHistory: options.maxHistory || 100
    };
  }
  
  update(frequencies: Uint8Array): void {
    // 添加当前频谱数据到历史记录
    this.history.unshift(new Uint8Array(frequencies));
    
    // 限制历史记录长度
    if (this.history.length > this.options.maxHistory) {
      this.history.pop();
    }
    
    // 清除画布
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
    
    // 计算每条历史记录的高度
