const CardDescription = styled.p`
  color: #bbb;
  margin-bottom: 20px;
  font-size: 0.9rem;
  line-height: 1.5;
`;

const CardFooter = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CardStats = styled.div`
  display: flex;
  gap: 15px;
  color: #999;
  font-size: 0.8rem;
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 5px;
`;

const ViewButton = styled(Link)`
  background-color: #9c27b0;
  color: white;
  padding: 8px 15px;
  border-radius: 4px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: background-color 0.2s ease;
  
  &:hover {
    background-color: #ba68c8;
  }
`;

